// use state hook to create a state
import {useState} from 'react';

import Layout from '../components/Layout';


// function Home() {
//   return <div>SRE To-do List</div>;
// }

// refactor above function with arrow function
//replace <div> with <Layout>
const Register = () => {

  // create a state
  const [state, setState] = useState({
    name: '',
    email: '',
    password: '',
    error: '',
    success: '',
    bottomText: 'Register'
  })



  const registerForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <input onChange={handleChange('name')} type="text" className="form-control" placeholder="Type your name" />
      </div>
      <div className="form-group">
        <input onChange={handleChange('email')} type="email" className="form-control" placeholder="Type your email" />
      </div>
      <div className="form-group">
        <input onChange={handleChange('password')} type="password" className="form-control" placeholder="Type your password" />
      </div>
      <div className="form-group">
        <button className="btn btn-outline-dark">
          Register
        </button>
      </div>

    </form>
  )

  return <Layout>
    <div className="col-md-6 offset-md-3">
      <h1>Register</h1>
      <br />
      {registerForm()}
    </div>
  </Layout>;
}


export default Register;
